package decisionmaking;

public class ifelseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=22,b=10,c=50;
		if(a>b && a>c)
		{
			System.out.print(a);
		}
		else if(b>a && b>c)
		{
			System.out.print(b);
		}
		else
		{
			System.out.print(c);
		}
	}

}
